        public static function getSQL()
        {
            return 'WITH WTRM AS (
SELECT T.*, 
       ROW_NUMBER() OVER (PARTITION BY W_ID_H ORDER BY last_date DESC NULLS LAST) rn
  FROM (       
SELECT DISTINCT T.*,
       NVL(PWTV.Value,MWTV.Value) last_value,
       NVL(PWTV.CDate,MWTV.Date_)  last_date,
       PREFIX||\',\'||
         NVL(PWT.NAME,
             Case
               when T.DateTest < SYSDATE then \'НЕТ ИПУ\'
               else case MWT.CounterType when 0 then \'ХВ\' when 1 then \'ГВ\' end || nvl(MWT.Numpipe, 1)
             end
            ) NAME,
      NVL(PWTV.USERNAME,MWTV.INPUTUSER) USR
  FROM (
select distinct
       MH.PREFIX,
       TO_NUMBER(SUBSTR(MH.EXTERNAL_ID,2)) W_ID_H,
       MH.ID RVKP_W_ID,  
       CASE WHEN EW.TRANSBLOCK<1 THEN EW.TRANSBLOCK
            WHEN NOT (EW.accdate <= trunc(sysdate,\'mm\') AND EW.endaccdate > trunc(sysdate,\'mm\')) THEN -1 
            ELSE 1
       END TRANSBLOCK,
       MAX(EV.INDICATION) keep (dense_rank first  ORDER BY EV.ACCDATE DESC) OVER(PARTITION BY MH.ID) RVKP_LAST_VALUE,
       MAX(EV.ACCDATE) OVER(PARTITION BY MH.ID) RVKP_LAST_DATE,
       MAX(EV2.ACCDATE) OVER(PARTITION BY MH.ID) DATETEST,
       MAX(EW.SERIAL_NUMBER) OVER(PARTITION BY MH.ID) SerNum
from MAPS_HIST MH
JOIN EN_WATERMETERS EW 
  ON EW.ID=MH.ID
join attr_ids a_o on a_o.VALUE = EW.id 
 and a_o.transblock >= 1 and a_o.accdate <= ew.accdate 
 and a_o.type_id IN (SELECT MT.id 
                       FROM mc_types MT 
                      WHERE MT.name in (\'ПриборУчетаПоТрубе\',\'ПриборУчетаПоТрубеКомб\',\'ПриборУчетаПоТрубеОбр\')
                     )
 and a_o.endaccdate >  ew.accdate
JOIN en_pipes pip 
  ON a_o.obj_id = pip.id 
JOIN en_water_objects wo
  ON wo.id = pip.water_object_id
join en_fact_specifications fs 
  on pip.id=fs.pipe_id 
join en_consumption_points cp 
  on fs.consume_point_id = cp.id
JOIN DOCUMENTS jur
  on cp.jur_ls_squid = jur.squid    
LEFT JOIN en_watermeter_values EV
       ON EV.WATERMETER_ID=MH.ID AND EV.transblock >= 1
     AND (SELECT MAX(T4.NAME)
             FROM mc_types t4
            WHERE t4.id= EV.type_id
          )=\'Показание\'
left JOIN mc_types t3
       ON t3.id= EV.type_id
LEFT JOIN en_watermeter_values EV2
       ON EV2.WATERMETER_ID=MH.ID AND EV2.transblock >= 1 
      AND (SELECT MAX(T4.NAME)
             FROM mc_types t4
            WHERE t4.id= EV2.type_id
          )=\'ОкончаниеПоверки\'        
WHERE MH.TABLE_NAME=\'load_en_watermeters\'
  AND MH.PREFIX IN (\'P\',\'M\')
  AND cp.jur_ls_squid=:5
  AND \'MP\'||JUR.DID=:7
) T
LEFT JOIN prvp.prvCounter@RH PWT
  ON PWT.ID=CASE WHEN PREFIX=\'P\' THEN T.W_ID_H
               ELSE -777
             END
LEFT JOIN prvp.prvCounterValue@RH PWTV
  ON PWTV.prvCounterId=PWT.ID  
LEFT JOIN mvCounter@RH MWT
  ON MWT.ID=CASE WHEN PREFIX=\'M\' THEN T.W_ID_H
               ELSE -777
             END
LEFT JOIN mvCounterValue@RH MWTV
  ON MWTV.mvCounter=MWT.ID 
) T),

WTRM_H AS (
SELECT T.*,
       ROW_NUMBER() OVER (PARTITION BY W_ID_H ORDER BY last_date DESC NULLS LAST) rn,
       MH.ID RVKP_W_ID
FROM (       
Select distinct 
      \'D\' PREFIX,
      c.Id W_ID_H,
      \'D,\'||Case when c.DateTest < SYSDATE then \'НЕТ ИПУ\'
           else Case when c.isFict > 0 then \'Фикт.\' end || case c.CounterType when 0 then \'ХВ\' when 1 then \'ГВ\' end || nvl(c.Numpipe, 1)
      end Name,            
      nvl(c.SerNum, \'ID\' || c.Id) SerNum,
      c.DateTest,
      c.CounterType,
      nvl(c.NumPipe, 1) NumPipe,
      cv.Value last_value,
      cv.Date_ last_date,
      Pac_minMax.leastNVL@RH(c.EndDate, case when ds.NewContractNum is not Null then ds.DieDay end) closeDate,
      cv.INPUTUSER USR
from Reality_Object_link_SOI@RH l
join Reality_Object@RH RO 
  on ro.Id = l.Reality_Object
join DSubdivision@RH DS 
  on DS.Id = l.Link_Id and l.LinkType = \'D\' --and ds.mvHouseAll is not Null
join dPrepare@RH P 
  on p.Id = ds.Prepare  
left join prvp.prvHouseAddress@RH ha 
  on ha.MainAccountId = ds.AccountId --для проверки что подразделение - не сам дом
join (Select ServiceType, case ServiceType when \'W\' then \'ХВ\' when \'C\' then \'Стоки\' when \'S\' then \'ГВ\' end Name from mvServiceType@RH s) ST 
  on (1=1)
join DInputDevice@RH c 
  on c.Subdivision = ds.Id and c.CounterType = mv_Calc.CounterTypeByService@RH(ST.ServiceType) and C.MetricType = 1 and ActDate <= Pac_Month.Month_To_date@RH(TO_NUMBER(To_CHAR(SYSDATE,\'RRRRMM\')), 1)      
LEFT JOIN dinputdeviceValue@RH cv
  ON cv.DInputDevice = c.Id
where c.Subdivision=:5
  AND \'D\'||P.Contract=:7
  AND ha.Id is null and ST.ServiceType <> \'C\'
  and C.Id is not Null
   and Exists(Select 1 from DSubdivision@RH DS, DPrepare@RH P where DS.Id = l.Link_ID and P.Id = DS.Prepare and P.Status = 2 and (DS.isConstruct <> 2 or DS.DSubdivType = 37))
   and (ST.ServiceType = \'C\' 
        or (ST.ServiceType <> \'C\' and c.Id is not Null)
        or Exists(Select 1 from DOutTariffs@RH dot join Tariff@RH t on t.Id = dot.Tariff
                   where dot.DSubdivision = ds.Id and (t.InspType in (1, 2, 3) and ST.ServiceType = \'W\' or t.InspType in (5, 6) and ST.ServiceType = \'S\')))
   and (Pac_minMax.leastNVL@RH(c.EndDate, case when ds.NewContractNum is not Null then ds.DieDay end) is Null
        or Pac_minMax.leastNVL@RH(c.EndDate, case when ds.NewContractNum is not Null then ds.DieDay end) > Pac_Month.Month_To_date@RH(TO_NUMBER(To_CHAR(SYSDATE,\'RRRRMM\')), 1)
        or Exists(Select 1 from DInvoice@RH where DInputDevice = C.Id and MonthYear <= TO_NUMBER(To_CHAR(SYSDATE,\'RRRRMM\')) and Volume <> 0)
        or nvl(c.StartDate, c.ActDate) < cvDate@RH(Pac_minMax.leastNVL@RH(c.DateTest, c.endDate, case when ds.NewContractNum is not Null then ds.DieDay end))
           and exists(Select 1 from DInputDeviceValue@RH 
               where DInputDevice = c.Id and Value is not Null 
                 and Date_ between nvl(c.StartDate, c.ActDate) and nvl(cvDate@RH(Pac_minMax.leastNVL@RH(c.DateTest, c.endDate, case when ds.NewContractNum is not Null then ds.DieDay end)), To_Date(\'01.01.4000\',\'DD.MM.RRRR\'))))
) T
LEFT JOIN MAPS_HIST MH
       ON MH.TABLE_NAME=\'load_en_watermeters\' AND MH.EXTERNAL_ID=\'D\'||TO_CHAR(T.W_ID_H)
      AND MH.PREFIX=\'D\'
)

SELECT W_ID_H,
       RVKP_W_ID,
       SERNUM,
       NAME,
       TO_CHAR(DATETEST, \'DD.MM.RRRR\') DATETEST,
       LAST_VALUE,
       TO_CHAR(LAST_DATE, \'DD.MM.RRRR\') LAST_DATE,
       VOLUME,
       RVKP_LAST_VALUE,
       TO_CHAR(RVKP_LAST_DATE, \'DD.MM.RRRR\') RVKP_LAST_DATE,
       case when (NVL(DATETEST,TO_DATE(\'01.01.4000\',\'DD.MM.RRRR\'))<trunc(sysdate,\'mm\'))               
                OR (last_value IS NULL AND RVKP_LAST_VALUE IS NULL)
                OR (NVL(closeDate,TO_DATE(\'01.01.4000\',\'DD.MM.RRRR\'))<trunc(sysdate,\'mm\'))
                OR (NVL(TRANSBLOCK,1)<1) THEN \'\'
              /*when NVL(last_value,-7777777)<NVL(RVKP_LAST_VALUE,-7777777) then 
                \'<input type="number" class="tab_input" prefix="\'||PREFIX||\'" name="lv_\'||RVKP_W_ID||\'" id="lv_\'||RVKP_W_ID||\'" id_h="\'||W_ID_H||\'" value="\'||TO_CHAR(RVKP_LAST_VALUE)||\'" maxlength="10" min="0" autocomplete="off">\'
           else \'<input type="number" class="tab_input" prefix="\'||PREFIX||\'" name="lv_\'||RVKP_W_ID||\'" id="lv_\'||RVKP_W_ID||\'" id_h="\'||W_ID_H||\'" value="\'||TO_CHAR(last_value)||\'" maxlength="10" min="0" autocomplete="off">\'*/
           else \'<input type="number" class="tab_input" prefix="\'||PREFIX||\'" name="lv_\'||RVKP_W_ID||\'" id="lv_\'||RVKP_W_ID||\'" id_h="\'||W_ID_H||\'" value="" maxlength="10" min="0" autocomplete="off">\'
       end new_value,
       \'<input type="date" class="tab_input" name="ld_\'||RVKP_W_ID||\'" id="ld_\'||RVKP_W_ID||\'" id_h="\'||W_ID_H||\'" value="\'||TO_CHAR(LAST_DAY(SYSDATE), \'RRRR-MM-DD\')||\'" autocomplete="off">\' new_date,
       0 NEW_VOLUME,
       USR 
FROM (
SELECT *
FROM (
SELECT W.PREFIX,
         W.W_ID_H,
         W.RVKP_W_ID,
         W.SERNUM,
         W.NAME,
         W.DATETEST,
         W.LAST_VALUE,
         W.LAST_DATE,
         CASE WHEN W.LAST_VALUE IS NOT NULL THEN W.LAST_VALUE-NVL(W2.LAST_VALUE,0)
            ELSE 0 
         END VOLUME,
         W.RVKP_LAST_VALUE,
         W.RVKP_LAST_DATE,
         NULL closeDate,
         W.TRANSBLOCK,
         W.USR
    FROM WTRM W
    LEFT JOIN WTRM W2 
      ON W2.RVKP_W_ID=W.RVKP_W_ID 
     AND W2.RN=2 
   WHERE W.RN=1    
ORDER BY W.NAME,W.RVKP_W_ID
)               
union all
SELECT PREFIX,
       W.W_ID_H,
       W.RVKP_W_ID,
       W.SERNUM,
       W.NAME,
       W.DATETEST,
       W.LAST_VALUE,
       W.LAST_DATE,
       W.VOLUME,
       W.RVKP_LAST_VALUE,
       W.RVKP_LAST_DATE,
       closeDate,
       NULL TRANSBLOCK,
       W.USR
FROM (       
SELECT DISTINCT 
       W.PREFIX,
       W.W_ID_H,
       W.RVKP_W_ID,
       W.SERNUM,
       W.NAME,
       W.DATETEST,
       W.LAST_VALUE,
       W.LAST_DATE,
       CASE WHEN W.LAST_VALUE IS NOT NULL THEN W.LAST_VALUE-NVL(W2.LAST_VALUE,0)
          ELSE 0 
       END VOLUME,
       MAX(EV.INDICATION) keep (dense_rank first  ORDER BY EV.ACCDATE DESC) OVER(PARTITION BY W.RVKP_W_ID) RVKP_LAST_VALUE,
       MAX(EV.ACCDATE) OVER(PARTITION BY W.RVKP_W_ID) RVKP_LAST_DATE,
       W.CounterType, 
       W.NumPipe,
       W.closeDate,
       W.USR
  FROM WTRM_H W
  LEFT JOIN WTRM_H W2 
    ON W2.W_ID_H=W.W_ID_H 
   AND W2.RN=2
  LEFT JOIN en_watermeter_values EV
    ON EV.WATERMETER_ID=W.RVKP_W_ID AND EV.transblock >= 1
  left JOIN mc_types t3
       ON t3.id= EV.type_id 
where (t3.name=\'Показание\' or t3.name is null) 
  AND W.RN=1
order by W.CounterType, W.NumPipe,W.W_ID_H  
)  W
) T';
        }
    }